import React, { useEffect, useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import './App.css';

function Resultados() {
  const [hoteles, setHoteles] = useState([]);
  const [destino, setDestino] = useState('');
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    const query = new URLSearchParams(location.search);
    const destinoQuery = query.get('destino') || '';
    setDestino(destinoQuery);

    fetch(`http://<SPRING_SERVER>/api/hoteles?destino=${destinoQuery}`)
      .then(res => res.json())
      .then(data => setHoteles(data))
      .catch(err => console.error('Error al cargar hoteles', err));
  }, [location.search]);

  const irAHotel = (id) => {
    navigate(`/hotel/${id}`);
  };

  return (
    <div className="resultados">
      <header>
        <input
          placeholder="¿A dónde vas?"
          value={destino}
          onChange={(e) => setDestino(e.target.value)}
        />
        <button onClick={() => navigate(`/resultados?destino=${encodeURIComponent(destino)}`)}>
          Buscar
        </button>
      </header>

      <h2>Resultados para: {destino}</h2>
      <div className="lista-hoteles">
        {hoteles.map(hotel => (
          <div key={hotel.id} className="hotel-card" onClick={() => irAHotel(hotel.id)}>
            <h3>{hotel.nombre}</h3>
            <p>Calificación: {hotel.calificacion}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Resultados;
