import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import './App.css';

function Hotel() {
  const { id } = useParams();
  const [hotel, setHotel] = useState(null);

  useEffect(() => {
    fetch(`http://<SPRING_SERVER>/api/hoteles/${id}`)
      .then(res => res.json())
      .then(data => setHotel(data))
      .catch(err => console.error("Error al obtener detalles del hotel", err));
  }, [id]);

  if (!hotel) return <div>Cargando...</div>;

  return (
    <div className="hotel-detalle">
      <header>
        <input placeholder="¿A dónde vas?" />
        <button>Buscar</button>
      </header>

      <h1>{hotel.nombre}</h1>
      <p>Calificación: {hotel.calificacion}</p>

      <h2>Tipos de habitación</h2>
      <ul>
        {hotel.habitaciones?.map((hab, i) => (
          <li key={i}>
            <strong>{hab.tipo}</strong>: {hab.descripcion} - ${hab.precio} MXN
          </li>
        ))}
      </ul>

      <h2>Opiniones</h2>
      <ul>
        {hotel.opiniones?.map((op, i) => (
          <li key={i}><em>{op.usuario}</em>: {op.comentario}</li>
        ))}
      </ul>
    </div>
  );
}

export default Hotel;
