          onChange={handleRegistroChange}
          required
          style={{
            width: '90%',
            padding: '8px',
            marginBottom: '10px',
            borderRadius: '6px',
            border: '1px solid #ccc'
          }}
        />

        <input
          type="email"
          name="email"
          placeholder="E-mail"
          value={registroData.email}
          onChange={handleRegistroChange}
          required
          style={{
            width: '90%',
            padding: '8px',
            marginBottom: '10px',
            borderRadius: '6px',
            border: '1px solid #ccc'
          }}
        />

        <input
          type="tel"
          name="phone"
          placeholder="Teléfono"
          value={registroData.phone}
          onChange={handleRegistroChange}
          style={{
            width: '90%',
            padding: '8px',
            marginBottom: '10px',
            borderRadius: '6px',
            border: '1px solid #ccc'
          }}
        />

        <input
          type="date"
          name="birthdate"
          placeholder="Fecha de nacimiento"
          value={registroData.birthdate}
          onChange={handleRegistroChange}
          style={{
            width: '90%',
            padding: '8px',
            marginBottom: '10px',
            borderRadius: '6px',
            border: '1px solid #ccc'
          }}
        />

        <input
          type="text"
          name="nationality"
          placeholder="Nacionalidad"
          value={registroData.nationality}
          onChange={handleRegistroChange}
          style={{
            width: '90%',
            padding: '8px',
            marginBottom: '10px',
            borderRadius: '6px',
            border: '1px solid #ccc'
          }}
        />

        <input
          type="text"
          name="passport_number"
          placeholder="Número de pasaporte"
          value={registroData.passport_number}
          onChange={handleRegistroChange}
          style={{
            width: '90%',
            padding: '8px',
            marginBottom: '10px',
            borderRadius: '6px',
            border: '1px solid #ccc'
          }}
        />

        <input
          type="password_hash"
          name="password_hash"
          placeholder="Contraseña"
          value={registroData.password_hash}
          onChange={handleRegistroChange}
          required
          style={{
            width: '90%',
            padding: '8px',
            marginBottom: '10px',
            borderRadius: '6px',
            border: '1px solid #ccc'
          }}
        />

        <input
          type="password_hash"
          name="confirmPassword"
          placeholder="Confirmar contraseña"
          value={registroData.confirmPassword}
          onChange={handleRegistroChange}
          required
          style={{
            width: '90%',
            padding: '8px',
            marginBottom: '16px',
            borderRadius: '6px',
            border: '1px solid #ccc'
          }}
        />

        <button
          type="submit"
          style={{
            width: '100%',
            padding: '8px',
            borderRadius: '6px',
            border: '1px solid #f9c300',
            background: '#f9c300',
            color: '#fff',
            cursor: 'pointer'
          }}
        >
          Registrar
        </button>
        <button
          type="button"
          style={{
            width: '100%',
            marginTop: '8px',
            padding: '8px',
            borderRadius: '6px',
            border: '1px solid #ccc',
            background: '#fff',
            color: '#f9c300',
            cursor: 'pointer'
          }}
          onClick={() => setMostrarRegistro(false)}
        >
          Volver
        </button>
      </form>
    )}
  </div>
)}
      {popupReservasAbierto && (
  <div
    className="pestana-emergente"
    style={{
      position: 'absolute',
      top: '50px',
      right: '50px',
      background: '#fff',
      color: '#222',
      borderRadius: '10px',
      boxShadow: '0 2px 8px rgba(0,0,0,0.15)',
      padding: '20px',
      zIndex: 2000,
      minWidth: '260px',
      textAlign: 'center'
    }}
  >
    {!usuario ? (
      <>
        <p>No has iniciado sesión. Por favor, inicia sesión para ver tus reservas.</p>
        <button
          style={{
            width: '100%',
            marginBottom: '10px',
            padding: '8px',
            borderRadius: '6px',
            border: '1px solid #f9c300',
            background: '#f9c300',
            color: '#fff',
            cursor: 'pointer'
          }}
          onClick={() => {
            setPopupReservasAbierto(false);
            setPerfilAbierto(true);
            setMostrarLogin(true);
          }}
        >
          Iniciar sesión
        </button>
        <p style={{ fontSize: '0.9em', margin: '10px 0' }}>
          o, si no te has registrado, ¡regístrate!
        </p>
        <button
          style={{
            width: '100%',
            padding: '8px',
            borderRadius: '6px',
            border: '1px solid #f9c300',
            background: '#fff',
            color: '#f9c300',
            cursor: 'pointer'
          }}
          onClick={() => {
            setPopupReservasAbierto(false);
            setPerfilAbierto(true);
            setMostrarRegistro(true);
          }}
        >
          Registrarse
        </button>
      </>
    ) : (
      <>
        <h3>Mis Reservas</h3>
        {reservas.length === 0 ? (
          <p>No tienes reservaciones.</p>
        ) : (
          <ul style={{ listStyle: 'none', padding: 0, maxHeight: '400px', overflowY: 'auto', textAlign: 'left' }}>
            {reservas.map((r, i) => (
              <li key={i} style={{ marginBottom: '16px', padding: '12px', border: '1px solid #ccc', borderRadius: '6px' }}>
                <p><strong>ID Reserva:</strong> {r.reservationId}</p>
                <p><strong>Hotel:</strong> {r.hotelName}</p>
                <p><strong>Habitaciones:</strong> {r.number}</p>
                <p><strong>Tipo:</strong> {r.roomType}</p>
                <p><strong>Check-In:</strong> {r.checkIn}</p>
                <p><strong>Check-Out:</strong> {r.checkOut}</p>
                <p><strong>Estado:</strong> {r.reservationStatus}</p>
                <p><strong>Precio Final:</strong> ${r.finalPrice}</p>
                <button className='boton'
                  onClick={() => {
                    setUsuario(null);
                    localStorage.removeItem("usuario");
                    setPopupReservasAbierto(false);
                  }}
                >
                  Cerrar sesión
                </button>
              </li>
            ))}
          </ul>
        )}
      </>
    )}
  </div>
)}
      </header>

      <main style={{ paddingTop: "60px" }}>
        <div className="funciones" style={{ display: 'flex', gap: '20px', justifyContent: 'center', margin: '20px 0' }}>
          <div style={{ position: 'relative' }}>
            <button
              className="funcion-boton"
              onClick={() => setPestanaActiva(pestanaActiva === 'destino' ? null : 'destino')}
            >
              <img src="https://img.icons8.com/ios-filled/50/ffffff/airport.png" alt="avión" />
              ¿A dónde vas? {destino && `→ ${destino}`}
            </button>

            {pestanaActiva === 'destino' && (
              <div className="popup-destinos">
                <h3 className="popup-titulo">Destinos Populares</h3>
                <ul className="popup-lista">
                  {[
                    "Acapulco, Guerrero - México",
                    "Cancún, Quintana Roo - México",
                    "Cuernavaca, Morelos - México",
                    "Puerto Vallarta, Jalisco - México",
                    "Huatulco, Oaxaca - México"
                  ].map((dest, i) => (
                    <li
                      key={i}
                      className={`popup-item ${destino === dest ? 'seleccionado' : ''}`}
                      onClick={() => {
                        setDestino(dest);
                        setPestanaActiva(null);
                      }}
                    >
                      <img
                        src="https://img.icons8.com/ios-filled/24/f9c300/marker.png"
                        alt="ubicación"
                        className="icono-ubicacion"
                      />
                      <span>{dest}</span>
                    </li>
                  ))}
                </ul>
              </div>
            )}
          </div>

          <div style={{ position: 'relative' }}>
            <button
              className="funcion-boton"
              onClick={() => setPestanaActiva(pestanaActiva === 'fechas' ? null : 'fechas')}
            >
              <img src="https://img.icons8.com/ios-filled/50/ffffff/calendar.png" alt="calendario" />
              {llegada && salida
                ? `${new Date(llegada).toLocaleDateString()} - ${new Date(salida).toLocaleDateString()}`
                : 'Llegada - Salida'}
            </button>

            {pestanaActiva === 'fechas' && (
              <div className="pestana-emergente" style={{ position: 'absolute', top: '50px', left: 0 }}>
                <label>
                  <b>Llegada:</b>
                  <input
                    type="datetime-local"
                    className="campo-fecha"
                    value={llegada}
                    onChange={e => setLlegada(e.target.value)}
                  />
                </label>
                <br />
                <label>
                  <b>Salida:</b>
                  <input
                    type="datetime-local"
                    className="campo-fecha"
                    value={salida}
                    onChange={e => setSalida(e.target.value)}
                  />
                </label>
              </div>
            )}
          </div>

          <div style={{ position: 'relative' }}>
            <button
              className="funcion-boton"
              onClick={() => setPestanaActiva(pestanaActiva === 'personas' ? null : 'personas')}
            >
              <img src="https://img.icons8.com/ios-filled/50/ffffff/conference.png" alt="personas" />
              Niños - Adultos - Habitaciones
            </button>

            {pestanaActiva === 'personas' && (
              <div className="pestana-emergente-huespedes">
                <div className="fila-huespedes">
                  <span className="etiqueta">Adultos</span>
                  <div className="control">
                    <button onClick={() => setAdultos(Math.max(1, adultos - 1))}>-</button>
                    <span>{adultos}</span>
                    <button onClick={() => setAdultos(adultos + 1)}>+</button>
                  </div>
                </div>
                <hr />
                <div className="fila-huespedes">
                  <span className="etiqueta">Niños</span>
                  <div className="control">
                    <button onClick={() => setNinos(Math.max(0, ninos - 1))}>-</button>
                    <span>{ninos}</span>
                    <button onClick={() => setNinos(ninos + 1)}>+</button>
                  </div>
                </div>
                <hr />
                <div className="fila-huespedes">
                  <span className="etiqueta">Habitaciones</span>
                  <div className="control">
                    <button onClick={() => setHabitaciones(Math.max(1, habitaciones - 1))}>-</button>
                    <span>{habitaciones}</span>
                    <button onClick={() => setHabitaciones(habitaciones + 1)}>+</button>
                  </div>
                </div>
                <button className="boton-buscar-popup" onClick={handleBuscar}>Buscar</button>
              </div>
            )}
          </div>
        </div>

        <h1 style={{ textAlign: 'center' }}>{hotel.name}</h1>
      <p style={{ textAlign: 'center' }}>Estrellas: {hotel.stars}</p>
      <p style={{ textAlign: 'center' }}>Ciudad: {hotel.city}, {hotel.country}</p>
      <p style={{ textAlign: 'center' }}>Descripción: {hotel.description}</p>
      <p style={{ textAlign: 'center' }}>Teléfono: {hotel.phone}</p>
      <p style={{ textAlign: 'center' }}>Email: {hotel.email}</p>

      <h2>Tipos de habitación</h2>
      <ul>
        {roomTypes.map((type, i) => (
          <li key={i}>
            <strong>{type.name}</strong>: {type.description}<br />
            Capacidad Máx: {type.maxCapacity}<br />
            Amenidades:
            <ul>
              {type.amenityCreates?.map((amenity, j) => (
                <li key={j}>{amenity.name} {amenity.icon && <span>{amenity.icon}</span>}</li>
              ))}
            </ul>
          </li>
        ))}
      </ul>
      {/*
      <ul>
        {hotel.roomNoDetail?.map((room, i) => (
          <li key={i}>
            <strong>{room.roomTypeID.name}</strong>: {room.roomTypeID.description} <br />
            Capacidad Máx: {room.roomTypeID.maxCapacity} <br />
            Habitación #{room.number} - ${room.basePrice} MXN
            {room.roomTypeID.amenities?.length > 0 && (
              <>
                <br /><span>Amenidades:</span>
                <ul>
                  {room.roomTypeID.amenities.map((amenity, j) => (
                    <li key={j}>{amenity.name} {amenity.icon && <span>{amenity.icon}</span>}</li>
                  ))}
                </ul>
              </>
            )}
          </li>
        ))}
      </ul>
      */}

      <h2>Promociones</h2>
      <ul>
        {hotel.promotionDTOS?.map((promo, i) => (
          <li key={i}>
            <strong>{promo.code}</strong> - {promo.discountPercent}% de descuento<br />
            Vigencia: {promo.startDate} - {promo.endDate}
          </li>
        ))}
      </ul>

      {/* Botón Reservar */}
      <div style={{ textAlign: 'center', marginBottom: '20px' }}>
        <button
          className="boton-buscar-popup"
          onClick={() => setPopupReservationForm(true)}
        >
          Reservar
        </button>
      </div>

      </main>

      {/* Popup de Reserva */}
      {popupReservationForm && (
        <div
          className="pestana-emergente"
          style={{
            position: 'fixed',
            top: '50%',
            left: '50%',
            transform: 'translate(-50%, -50%)',
            background: '#fff',
            color: '#222',
            borderRadius: '10px',
            boxShadow: '0 2px 8px rgba(0,0,0,0.15)',
            padding: '30px',
            zIndex: 3000,
            width: '600px',
            maxHeight: '80vh',
            overflowY: 'auto'
          }}
        >
          {!usuario ? (
            <>
              <p>No has iniciado sesión. Por favor, inicia sesión para hacer una reserva.</p>
              <button onClick={() => { setPopupReservationForm(false); setMostrarLogin(true); setPerfilAbierto(true); }}>
                Iniciar sesión
              </button>
            </>
          ) : (
            <>
              <form onSubmit={e => {
                e.preventDefault();
                handleReservate();
              }}>
                <h3>Hacer Reservación</h3>
                <label>Tipo de Habitación:</label>
                <select value={resRoomTypeId} onChange={e => setResRoomTypeId(e.target.value)} required>
                  <option value="">Selecciona</option>
                  {roomTypes.map(rt => (
                    <option key={rt.id} value={rt.id}>{rt.name}</option>
                  ))}
                </select>
                <br />
                <label>Cantidad de Habitaciones:</label>
                <input type="number" min="1" value={resRoomsCount} onChange={e => setResRoomsCount(parseInt(e.target.value))} required />
                <br />
                <label>Check-In:</label>
                <input type="date" value={resCheckIn} onChange={e => setResCheckIn(e.target.value)} required />
                <br />
                <label>Check-Out:</label>
                <input type="date" value={resCheckOut} onChange={e => setResCheckOut(e.target.value)} required />
                <br />
                <label>Código de Promoción (opcional):</label>
                <input type="text" value={resPromoCode} onChange={e => setResPromoCode(e.target.value)} />
                <br />
                <div style={{ marginTop: '20px' }}>
                  <button type="submit" className="boton-buscar-popup">Confirmar Reserva</button>
                  <button type="button" onClick={() => setPopupReservationForm(false)}>Cancelar</button>
                </div>
              </form>
              {reservationResponse && (
                <div style={{ marginTop: '20px', textAlign: 'left' }}>
                  <h4>Respuesta del Servidor:</h4>
                  <pre style={{ background: '#f9f9f9', padding: '10px', borderRadius: '6px', maxHeight: '200px', overflowY: 'auto' }}>
                    {JSON.stringify(reservationResponse, null, 2)}
                  </pre>
                </div>
              )}
            </>
          )}
        </div>
      )}

      <footer style={{ background: '#00163a', color: '#fff', padding: '20px', textAlign: 'center', marginTop: '30px' }}>
        <p><b>©2025 ClickRoom. Todos los derechos reservados.</b></p>
        <p>Desarrollado por Siminbikini</p>
      </footer>
    </div>
  );
}

export default Hotel;
