import React, { useEffect, useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import './App.css';
import logo from './img/ClickRoom.png';
import perfilImg from './img/perfil.jpg';

function Resultados() {
  const [hoteles, setHoteles] = useState([]);

  const [destino, setDestino] = useState('');
  const [llegada, setLlegada] = useState('');
  const [salida, setSalida] = useState('');
  const [adultos, setAdultos] = useState(0);
  const [ninos, setNinos] = useState(0);
  const [habitaciones, setHabitaciones] = useState(0);

  const navigate = useNavigate();
  const location = useLocation();

  const [pestanaActiva, setPestanaActiva] = useState(null);
  // Estados para login y registro
  const [perfilAbierto, setPerfilAbierto] = useState(false);
  const [mostrarLogin, setMostrarLogin] = useState(false);
  const [mostrarRegistro, setMostrarRegistro] = useState(false);
  const [usuario, setUsuario] = useState(null);
  const [popupReservasAbierto, setPopupReservasAbierto] = useState(false);
  const [reservas, setReservas] = useState([]);

  // Persistencia de usuario en localStorage
  useEffect(() => {
    const saved = localStorage.getItem("usuario");
    if (saved) {
      setUsuario(JSON.parse(saved));
    }
  }, []);

  const [loginData, setLoginData] = useState({ email: '', password: '' });
  const [registroData, setRegistroData] = useState({
    nombre: '',
    apellido: '',
    email: '',
    celular: '',
    password: '',
    confirmPassword: ''
  });

  useEffect(() => {
    const query = new URLSearchParams(location.search);
    const todos = query.get('todos');
    const destinoQuery = query.get('destino') || '';
    const llegadaQuery = query.get('llegada') || '';
    const salidaQuery = query.get('salida') || '';
    const adultosQuery = parseInt(query.get('adultos')) || 0;
    const ninosQuery = parseInt(query.get('ninos')) || 0;
    const habitacionesQuery = parseInt(query.get('habitaciones')) || 0;

    setDestino(destinoQuery);
    setLlegada(llegadaQuery);
    setSalida(salidaQuery);
    setAdultos(adultosQuery);
    setNinos(ninosQuery);
    setHabitaciones(habitacionesQuery);

    // Nueva lógica: POST con body para paginación y ordenamiento
    const endpoint = todos === 'true'
      ? "/hotel/search/all"
      : "/hotel/search";
    const body = todos === 'true'
      ? { page: 0, size: 10, sort: ["name"] }
      : { page: 0, size: 10, sort: ["name"], destino: destinoQuery, habitaciones: habitacionesQuery };

    fetch(endpoint, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(body)
    })
      .then(res => res.json())
      .then(data => {
        console.log("Respuesta del backend:", data);
        if (Array.isArray(data)) {
          setHoteles(data);
        } else {
          setHoteles([]);
        }
      })
      .catch(err => console.error('Error al cargar hoteles', err));
}, [location.search]);

  const handleBuscar = () => {
    const params = new URLSearchParams({
      destino,
      llegada,
      salida,
      adultos,
      ninos,
      habitaciones
    });
    navigate(`/resultados?${params.toString()}`);
  };
  const handleLogout = () => {
    setUsuario(null);
    localStorage.removeItem("usuario");
    setPerfilAbierto(false);
    setReservas([]);
  };
  const handleGlobalSearch = (e) => {
    if (e.key && e.key !== 'Enter') return;

    let params;
    if (!destino || destino.trim() === '') {
      // Si el campo está vacío, buscar todos los hoteles
      params = new URLSearchParams({
        todos: true
      });
    } else {
      params = new URLSearchParams({
        destino
      });
    }

    navigate(`/resultados?${params.toString()}`);

    if (setPestanaActiva) {
      setPestanaActiva(null);
    }
  };
  const handleLoginChange = (e) => {
    setLoginData({ ...loginData, [e.target.name]: e.target.value });
  };
  const handleLoginSubmit = (e) => {
    e.preventDefault();
    alert(`Email: ${loginData.email}\nContraseña: ${loginData.password}`);
    setUsuario(loginData);
    localStorage.setItem("usuario", JSON.stringify(loginData));
    setLoginData({ email: '', password: '' });
    setMostrarLogin(false);
    setPerfilAbierto(false);
  };
  const handleRegistroChange = (e) => {
    setRegistroData({ ...registroData, [e.target.name]: e.target.value });
  };
  const handleRegistroSubmit = (e) => {
    e.preventDefault();
    alert(
      `Nombre: ${registroData.nombre}\nApellido: ${registroData.apellido}\nEmail: ${registroData.email}\nCelular: ${registroData.celular}\nContraseña: ${registroData.password}\nConfirmación: ${registroData.confirmPassword}`
    );
    setRegistroData({
      nombre: '',
      apellido: '',
      email: '',
      celular: '',
      password: '',
      confirmPassword: ''
    });
    setMostrarRegistro(false);
    setPerfilAbierto(false);
  };
  const irAHotel = (hotelId) => {
    navigate(`/hotel/${hotelId}`);
  };

  return (
    <div className="App">
      <header className="App-header">
        <div className="logo">
          <a href="/">
            <img src={logo} alt="logo" />
          </a>
        </div>

        {/* Barra de búsqueda */}
        <div className="search-bar">
          <input
            type="text"
            placeholder="¿A dónde vas?"
            value={destino}
            onChange={e => setDestino(e.target.value)}
            onKeyDown={handleGlobalSearch}
          />
          <button
            onClick={handleGlobalSearch}
            className="boton-buscar-popup"
            style={{ marginLeft: '10px' }}
          >
            Buscar
          </button>
        </div>

        <div className="header-icons">
          <button
            onClick={() => setPopupReservasAbierto(!popupReservasAbierto)}
          >
            <span role="img" aria-label="reservas" style={{ fontSize: 24 }}>
              🛏️
            </span>
          </button>
          <img
            src={perfilImg}
            alt="perfil"
            style={{ cursor: 'pointer', borderRadius: '50%' }}
            onClick={() => {
              setPerfilAbierto(!perfilAbierto);
              setMostrarLogin(false);
              setMostrarRegistro(false);
            }}
          />
        </div>
        {perfilAbierto && (
  <div
    className="pestana-emergente"
    style={{
      position: 'absolute',
      top: '50px',
      right: 0,
      background: '#fff',
      color: '#222',
      borderRadius: '10px',
      boxShadow: '0 2px 8px rgba(0,0,0,0.15)',
      padding: '20px',
      zIndex: 2000,
      minWidth: '240px',
      textAlign: 'center'
    }}
  >
    {!mostrarLogin && !mostrarRegistro ? (
      usuario ? (
        <>
          <p style={{
            marginBottom: '16px',
            fontWeight: 'bold',
            color: '#00163a'
          }}>
            Hola, {usuario.firstName}!
          </p>
          <button
            style={{
              width: '100%',
              padding: '8px',
              borderRadius: '6px',
              border: '1px solid #f9c300',
              background: '#fff',
              color: '#f9c300',
              cursor: 'pointer'
            }}
            onClick={handleLogout}
          >
            Cerrar Sesión
          </button>
        </>
      ) : (
        <>
          <img
            src={perfilImg}
            alt="Perfil"
            style={{
              width: 60,
              height: 60,
              borderRadius: '50%',
              marginBottom: 16,
              objectFit: 'cover'
            }}
          />
          <div style={{ marginBottom: 12 }}>
            <button
              style={{
                width: '100%',
                marginBottom: 8,
                padding: '8px',
                borderRadius: '6px',
                border: '1px solid #f9c300',
                background: '#f9c300',
                color: '#fff',
                cursor: 'pointer'
              }}
              onClick={() => {
                setMostrarLogin(true);
                setMostrarRegistro(false);
              }}
            >
              Iniciar sesión
            </button>
            <button
              style={{
                width: '100%',
                padding: '8px',
                borderRadius: '6px',
                border: '1px solid #f9c300',
                background: '#fff',
                color: '#f9c300',
                cursor: 'pointer'
              }}
              onClick={() => {
                setMostrarRegistro(true);
                setMostrarLogin(false);
              }}
            >
              Registrarse
            </button>
          </div>
        </>
      )
    ) : mostrarLogin ? (
      <form onSubmit={handleLoginSubmit}>
        <h3 style={{ marginBottom: 16 }}>Iniciar sesión</h3>
        <input
          type="email"
          name="email"
          placeholder="E-mail"
          value={loginData.email}
          onChange={handleLoginChange}
          required
          style={{
            width: '90%',
            padding: '8px',
            marginBottom: '10px',
            borderRadius: '6px',
            border: '1px solid #ccc'
          }}
        />
        <input
          type="password"
          name="password"
          placeholder="Contraseña"
          value={loginData.password}
          onChange={handleLoginChange}
          required
          style={{
            width: '90%',
            padding: '8px',
            marginBottom: '16px',
            borderRadius: '6px',
            border: '1px solid #ccc'
          }}
        />
        <button
          type="submit"
          style={{
            width: '100%',
            padding: '8px',
            borderRadius: '6px',
            border: '1px solid #f9c300',
            background: '#f9c300',
            color: '#fff',
            cursor: 'pointer'
          }}
        >
          Iniciar sesión
        </button>
        <button
          type="button"
          style={{
            width: '100%',
            marginTop: '8px',
            padding: '8px',
            borderRadius: '6px',
            border: '1px solid #ccc',
            background: '#fff',
            color: '#f9c300',
            cursor: 'pointer'
          }}
          onClick={() => setMostrarLogin(false)}
        >
          Volver
        </button>
      </form>
    ) : (
      <form onSubmit={handleRegistroSubmit}>
        <h3 style={{ marginBottom: 16 }}>Registro</h3>

        <input
          type="text"
          name="first_name"
          placeholder="Nombre"
          value={registroData.first_name}
          onChange={handleRegistroChange}
          required
          style={{
            width: '90%',
            padding: '8px',
            marginBottom: '10px',
            borderRadius: '6px',
            border: '1px solid #ccc'
          }}
        />

        <input
          type="text"
          name="last_name"
          placeholder="Apellido"
          value={registroData.last_name}
          onChange={handleRegistroChange}
          required
          style={{
            width: '90%',
            padding: '8px',
            marginBottom: '10px',
            borderRadius: '6px',
            border: '1px solid #ccc'
          }}
        />

        <input
          type="email"
          name="email"
          placeholder="E-mail"
          value={registroData.email}
          onChange={handleRegistroChange}
          required
          style={{
            width: '90%',
            padding: '8px',
            marginBottom: '10px',
            borderRadius: '6px',
            border: '1px solid #ccc'
          }}
        />

        <input
          type="tel"
          name="phone"
          placeholder="Teléfono"
          value={registroData.phone}
          onChange={handleRegistroChange}
          style={{
            width: '90%',
            padding: '8px',
            marginBottom: '10px',
            borderRadius: '6px',
            border: '1px solid #ccc'
          }}
        />

        <input
          type="date"
          name="birthdate"
          placeholder="Fecha de nacimiento"
          value={registroData.birthdate}
          onChange={handleRegistroChange}
          style={{
            width: '90%',
            padding: '8px',
            marginBottom: '10px',
            borderRadius: '6px',
            border: '1px solid #ccc'
          }}
        />

        <input
          type="text"
          name="nationality"
          placeholder="Nacionalidad"
          value={registroData.nationality}
          onChange={handleRegistroChange}
          style={{
            width: '90%',
            padding: '8px',
            marginBottom: '10px',
            borderRadius: '6px',
            border: '1px solid #ccc'
          }}
        />

        <input
          type="text"
          name="passport_number"
          placeholder="Número de pasaporte"
          value={registroData.passport_number}
          onChange={handleRegistroChange}
          style={{
            width: '90%',
            padding: '8px',
            marginBottom: '10px',
            borderRadius: '6px',
            border: '1px solid #ccc'
          }}
        />

        <input
          type="password_hash"
          name="password_hash"
          placeholder="Contraseña"
          value={registroData.password_hash}
          onChange={handleRegistroChange}
          required
          style={{
            width: '90%',
            padding: '8px',
            marginBottom: '10px',
            borderRadius: '6px',
            border: '1px solid #ccc'
          }}
        />

        <input
          type="password_hash"
          name="confirmPassword"
          placeholder="Confirmar contraseña"
          value={registroData.confirmPassword}
          onChange={handleRegistroChange}
          required
          style={{
            width: '90%',
            padding: '8px',
            marginBottom: '16px',
            borderRadius: '6px',
            border: '1px solid #ccc'
          }}
        />

        <button
          type="submit"
          style={{
            width: '100%',
            padding: '8px',
            borderRadius: '6px',
            border: '1px solid #f9c300',
            background: '#f9c300',
            color: '#fff',
            cursor: 'pointer'
          }}
        >
          Registrar
        </button>
        <button
          type="button"
          style={{
            width: '100%',
            marginTop: '8px',
            padding: '8px',
            borderRadius: '6px',
            border: '1px solid #ccc',
            background: '#fff',
            color: '#f9c300',
            cursor: 'pointer'
          }}
          onClick={() => setMostrarRegistro(false)}
        >
          Volver
        </button>
      </form>
    )}
  </div>
)}
      {popupReservasAbierto && (
  <div
    className="pestana-emergente"
    style={{
      position: 'absolute',
      top: '50px',
      right: '50px',
      background: '#fff',
      color: '#222',
      borderRadius: '10px',
      boxShadow: '0 2px 8px rgba(0,0,0,0.15)',
      padding: '20px',
      zIndex: 2000,
      minWidth: '260px',
      textAlign: 'center'
    }}
  >
    {!usuario ? (
      <>
        <p>No has iniciado sesión. Por favor, inicia sesión para ver tus reservas.</p>
        <button
          style={{
            width: '100%',
            marginBottom: '10px',
            padding: '8px',
            borderRadius: '6px',
            border: '1px solid #f9c300',
            background: '#f9c300',
            color: '#fff',
            cursor: 'pointer'
          }}
          onClick={() => {
            setPopupReservasAbierto(false);
            setPerfilAbierto(true);
            setMostrarLogin(true);
          }}
        >
          Iniciar sesión
        </button>
        <p style={{ fontSize: '0.9em', margin: '10px 0' }}>
          o, si no te has registrado, ¡regístrate!
        </p>
        <button
          style={{
            width: '100%',
            padding: '8px',
            borderRadius: '6px',
            border: '1px solid #f9c300',
            background: '#fff',
            color: '#f9c300',
            cursor: 'pointer'
          }}
          onClick={() => {
            setPopupReservasAbierto(false);
            setPerfilAbierto(true);
            setMostrarRegistro(true);
          }}
        >
          Registrarse
        </button>
      </>
    ) : (
      <>
        <h3>Mis Reservas</h3>
        {reservas.length === 0 ? (
          <p>No tienes reservas registradas.</p>
        ) : (
          <ul style={{ listStyle: 'none', padding: 0, maxHeight: '200px', overflowY: 'auto' }}>
            {reservas.map((r, i) => (
              <li
                key={i}
                style={{
                  marginBottom: '10px',
                  padding: '10px',
                  border: '1px solid #ccc',
                  borderRadius: '6px'
                }}
              >
                <strong>{r.hotel}</strong><br />
                {new Date(r.llegada).toLocaleDateString()} - {new Date(r.salida).toLocaleDateString()}<br />
                Estado: {r.estado}
              </li>
            ))}
          </ul>
        )}
        <button
          style={{
            width: '100%',
            marginTop: '10px',
            padding: '8px',
            borderRadius: '6px',
            border: '1px solid #f9c300',
            background: '#fff',
            color: '#f9c300',
            cursor: 'pointer'
          }}
          onClick={() => {
            setUsuario(null);
            localStorage.removeItem("usuario");
            setPopupReservasAbierto(false);
          }}
        >
          Cerrar sesión
        </button>
      </>
    )}
  </div>
)}
      </header>

      <main style={{ paddingTop: "60px" }}>
        <div className="funciones" style={{ display: 'flex', gap: '20px', justifyContent: 'center', margin: '20px 0' }}>
          <div style={{ position: 'relative' }}>
            <button
              className="funcion-boton"
              onClick={() => setPestanaActiva(pestanaActiva === 'destino' ? null : 'destino')}
            >
              <img src="https://img.icons8.com/ios-filled/50/ffffff/airport.png" alt="avión" />
              ¿A dónde vas? {destino && `→ ${destino}`}
            </button>

            {pestanaActiva === 'destino' && (
              <div className="popup-destinos">
                <h3 className="popup-titulo">Destinos Populares</h3>
                <ul className="popup-lista">
                  {[
                    "Acapulco, Guerrero - México",
                    "Cancún, Quintana Roo - México",
                    "Cuernavaca, Morelos - México",
                    "Puerto Vallarta, Jalisco - México",
                    "Huatulco, Oaxaca - México"
                  ].map((dest, i) => (
                    <li
                      key={i}
                      className={`popup-item ${destino === dest ? 'seleccionado' : ''}`}
                      onClick={() => {
                        setDestino(dest);
                        setPestanaActiva(null);
                      }}
                    >
                      <img
                        src="https://img.icons8.com/ios-filled/24/f9c300/marker.png"
                        alt="ubicación"
                        className="icono-ubicacion"
                      />
                      <span>{dest}</span>
                    </li>
                  ))}
                </ul>
              </div>
            )}
          </div>

          <div style={{ position: 'relative' }}>
            <button
              className="funcion-boton"
              onClick={() => setPestanaActiva(pestanaActiva === 'fechas' ? null : 'fechas')}
            >
              <img src="https://img.icons8.com/ios-filled/50/ffffff/calendar.png" alt="calendario" />
              {llegada && salida
                ? `${new Date(llegada).toLocaleDateString()} - ${new Date(salida).toLocaleDateString()}`
                : 'Llegada - Salida'}
            </button>

            {pestanaActiva === 'fechas' && (
              <div className="pestana-emergente" style={{ position: 'absolute', top: '50px', left: 0 }}>
                <label>
                  <b>Llegada:</b>
                  <input
                    type="datetime-local"
                    className="campo-fecha"
                    value={llegada}
                    onChange={e => setLlegada(e.target.value)}
                  />
                </label>
                <br />
                <label>
                  <b>Salida:</b>
                  <input
                    type="datetime-local"
                    className="campo-fecha"
                    value={salida}
                    onChange={e => setSalida(e.target.value)}
                  />
                </label>
              </div>
            )}
          </div>

          <div style={{ position: 'relative' }}>
            <button
              className="funcion-boton"
              onClick={() => setPestanaActiva(pestanaActiva === 'personas' ? null : 'personas')}
            >
              <img src="https://img.icons8.com/ios-filled/50/ffffff/conference.png" alt="personas" />
              Niños - Adultos - Habitaciones
            </button>

            {pestanaActiva === 'personas' && (
              <div className="pestana-emergente-huespedes">
                <div className="fila-huespedes">
                  <span className="etiqueta">Adultos</span>
                  <div className="control">
                    <button onClick={() => setAdultos(Math.max(1, adultos - 1))}>-</button>
                    <span>{adultos}</span>
                    <button onClick={() => setAdultos(adultos + 1)}>+</button>
                  </div>
                </div>
                <hr />
                <div className="fila-huespedes">
                  <span className="etiqueta">Niños</span>
                  <div className="control">
                    <button onClick={() => setNinos(Math.max(0, ninos - 1))}>-</button>
                    <span>{ninos}</span>
                    <button onClick={() => setNinos(ninos + 1)}>+</button>
                  </div>
                </div>
                <hr />
                <div className="fila-huespedes">
                  <span className="etiqueta">Habitaciones</span>
                  <div className="control">
                    <button onClick={() => setHabitaciones(Math.max(1, habitaciones - 1))}>-</button>
                    <span>{habitaciones}</span>
                    <button onClick={() => setHabitaciones(habitaciones + 1)}>+</button>
                  </div>
                </div>
                <button className="boton-buscar-popup" onClick={handleBuscar}>Buscar</button>
              </div>
            )}
          </div>
        </div>

        <h2 style={{ textAlign: 'center' }}>Resultados para: {destino}</h2>

        <div style={{ textAlign: 'center', marginBottom: '20px' }}>
          <p>
            <b>Fechas:</b> {llegada && new Date(llegada).toLocaleDateString()} - {salida && new Date(salida).toLocaleDateString()}<br />
            <b>Adultos:</b> {adultos} &nbsp;
            <b>Niños:</b> {ninos} &nbsp;
            <b>Habitaciones:</b> {habitaciones}
          </p>
        </div>

        <div className="lista-hoteles" style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'center', gap: '20px' }}>
          {hoteles.length === 0 ? (
            <p style={{ textAlign: 'center' }}>No se encontraron hoteles para tu búsqueda.</p>
          ) : (
            hoteles.map(hotel => (
              <div
                key={hotel.id}
                className="hotel-card"
                onClick={() => irAHotel(hotel.id)}
                onMouseEnter={e => e.currentTarget.style.transform = 'scale(1.05)'}
                onMouseLeave={e => e.currentTarget.style.transform = 'scale(1)'}
              >
                <h3>{hotel.name}</h3>
                <p>Estrellas: {hotel.stars || 'N/A'}</p>
                <p>Ciudad: {hotel.city}, {hotel.country}</p>
                <p>Descripción: {hotel.description}</p>
                <p>Teléfono: {hotel.phone}</p>
                <p>Email: {hotel.email}</p>
              </div>
            ))
          )}
        </div>
      </main>

      <footer style={{ background: '#00163a', color: '#fff', padding: '20px', textAlign: 'center', marginTop: '30px' }}>
        <p><b>©2025 ClickRoom. Todos los derechos reservados.</b></p>
        <p>Desarrollado por Siminbikini</p>
      </footer>
    </div>
  );
}

export default Resultados;
