import React, { useEffect, useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import './App.css';
import logo from './img/ClickRoom.png';
import perfilImg from './img/perfil.jpg';

function Resultados() {
  const [hoteles, setHoteles] = useState([]);

  const [destino, setDestino] = useState('');
  const [llegada, setLlegada] = useState('');
  const [salida, setSalida] = useState('');
  const [adultos, setAdultos] = useState(0);
  const [ninos, setNinos] = useState(0);
  const [habitaciones, setHabitaciones] = useState(0);

  const navigate = useNavigate();
  const location = useLocation();

  const [pestanaActiva, setPestanaActiva] = useState(null);
  // Estados para login y registro
  const [perfilAbierto, setPerfilAbierto] = useState(false);
  const [mostrarLogin, setMostrarLogin] = useState(false);
  const [mostrarRegistro, setMostrarRegistro] = useState(false);

  const [loginData, setLoginData] = useState({ email: '', password: '' });
  const [registroData, setRegistroData] = useState({
    nombre: '',
    apellido: '',
    email: '',
    celular: '',
    password: '',
    confirmPassword: ''
  });

  useEffect(() => {
    const query = new URLSearchParams(location.search);
    const destinoQuery = query.get('destino') || '';
    const llegadaQuery = query.get('llegada') || '';
    const salidaQuery = query.get('salida') || '';
    const adultosQuery = parseInt(query.get('adultos')) || 0;
    const ninosQuery = parseInt(query.get('ninos')) || 0;
    const habitacionesQuery = parseInt(query.get('habitaciones')) || 0;

    setDestino(destinoQuery);
    setLlegada(llegadaQuery);
    setSalida(salidaQuery);
    setAdultos(adultosQuery);
    setNinos(ninosQuery);
    setHabitaciones(habitacionesQuery);

    fetch(`http://<SPRING_SERVER>/api/hoteles?destino=${encodeURIComponent(destinoQuery)}&llegada=${encodeURIComponent(llegadaQuery)}&salida=${encodeURIComponent(salidaQuery)}&adultos=${adultosQuery}&ninos=${ninosQuery}&habitaciones=${habitacionesQuery}`)
      .then(res => res.json())
      .then(data => setHoteles(data))
      .catch(err => console.error('Error al cargar hoteles', err));
  }, [location.search]);

  const irAHotel = (id) => {
    navigate(`/hotel/${id}`);
  };

  const handleBuscar = () => {
    const params = new URLSearchParams({
      destino,
      llegada,
      salida,
      adultos,
      ninos,
      habitaciones
    });
    navigate(`/resultados?${params.toString()}`);
  };
  const handleGlobalSearch = (e) => {
  if (e.key && e.key !== 'Enter') return;

  if (!destino || destino.trim() === '') {
    alert("Por favor ingresa un destino.");
    return;
  }

  const params = new URLSearchParams({
    destino
  });

  navigate(`/resultados?${params.toString()}`);

  if (setPestanaActiva) {
    setPestanaActiva(null);
  }
};
  const handleLoginChange = (e) => {
    setLoginData({ ...loginData, [e.target.name]: e.target.value });
  };
  const handleLoginSubmit = (e) => {
    e.preventDefault();
    alert(`Email: ${loginData.email}\nContraseña: ${loginData.password}`);
    setLoginData({ email: '', password: '' });
    setMostrarLogin(false);
    setPerfilAbierto(false);
  };
  const handleRegistroChange = (e) => {
    setRegistroData({ ...registroData, [e.target.name]: e.target.value });
  };
  const handleRegistroSubmit = (e) => {
    e.preventDefault();
    alert(
      `Nombre: ${registroData.nombre}\nApellido: ${registroData.apellido}\nEmail: ${registroData.email}\nCelular: ${registroData.celular}\nContraseña: ${registroData.password}\nConfirmación: ${registroData.confirmPassword}`
    );
    setRegistroData({
      nombre: '',
      apellido: '',
      email: '',
      celular: '',
      password: '',
      confirmPassword: ''
    });
    setMostrarRegistro(false);
    setPerfilAbierto(false);
  };

  return (
    <div className="App">
      <header className="App-header">
        <div className="logo">
          <a href="/">
            <img src={logo} alt="logo" />
          </a>
        </div>

        {/* Barra de búsqueda */}
        <div className="search-bar">
          <input
            type="text"
            placeholder="¿A dónde vas?"
            value={destino}
            onChange={e => setDestino(e.target.value)}
            onKeyDown={handleGlobalSearch}
          />
          <button
            onClick={handleGlobalSearch}
            className="boton-buscar-popup"
            style={{ marginLeft: '10px' }}
          >
            Buscar
          </button>
        </div>

        <div className="header-icons">
          <button>
            <img
              src="https://img.icons8.com/ios-filled/50/ffffff/settings.png"
              alt="configuración"
              style={{ width: 24, height: 24 }}
            />
          </button>
          <img
            src={perfilImg}
            alt="perfil"
            style={{ cursor: 'pointer', borderRadius: '50%' }}
            onClick={() => {
              setPerfilAbierto(!perfilAbierto);
              setMostrarLogin(false);
              setMostrarRegistro(false);
            }}
          />
        </div>
        {perfilAbierto && (
  <div
    className="pestana-emergente"
    style={{
      position: 'absolute',
      top: '50px',
      right: 0,
      background: '#fff',
      color: '#222',
      borderRadius: '10px',
      boxShadow: '0 2px 8px rgba(0,0,0,0.15)',
      padding: '20px',
      zIndex: 2000,
      minWidth: '240px',
      textAlign: 'center'
    }}
  >
    {!mostrarLogin && !mostrarRegistro ? (
      <>
        <img
          src={perfilImg}
          alt="Perfil"
          style={{ width: 60, height: 60, borderRadius: '50%', marginBottom: 16, objectFit: 'cover' }}
        />
        <div style={{ marginBottom: 12 }}>
          <button
            style={{
              width: '100%',
              marginBottom: 8,
              padding: '8px',
              borderRadius: '6px',
              border: '1px solid #f9c300',
              background: '#f9c300',
              color: '#fff',
              cursor: 'pointer'
            }}
            onClick={() => {
              setMostrarLogin(true);
              setMostrarRegistro(false);
            }}
          >
            Iniciar sesión
          </button>
          <button
            style={{
              width: '100%',
              padding: '8px',
              borderRadius: '6px',
              border: '1px solid #f9c300',
              background: '#fff',
              color: '#f9c300',
              cursor: 'pointer'
            }}
            onClick={() => {
              setMostrarRegistro(true);
              setMostrarLogin(false);
            }}
          >
            Registrarse
          </button>
        </div>
      </>
    ) : mostrarLogin ? (
      <form onSubmit={handleLoginSubmit}>
        <h3 style={{ marginBottom: 16 }}>Iniciar sesión</h3>
        <input
          type="email"
          name="email"
          placeholder="E-mail"
          value={loginData.email}
          onChange={handleLoginChange}
          required
          style={{
            width: '90%',
            padding: '8px',
            marginBottom: '10px',
            borderRadius: '6px',
            border: '1px solid #ccc'
          }}
        />
        <input
          type="password"
          name="password"
          placeholder="Contraseña"
          value={loginData.password}
          onChange={handleLoginChange}
          required
          style={{
            width: '90%',
            padding: '8px',
            marginBottom: '16px',
            borderRadius: '6px',
            border: '1px solid #ccc'
          }}
        />
        <button
          type="submit"
          style={{
            width: '100%',
            padding: '8px',
            borderRadius: '6px',
            border: '1px solid #f9c300',
            background: '#f9c300',
            color: '#fff',
            cursor: 'pointer'
          }}
        >
          Iniciar sesión
        </button>
        <button
          type="button"
          style={{
            width: '100%',
            marginTop: '8px',
            padding: '8px',
            borderRadius: '6px',
            border: '1px solid #ccc',
            background: '#fff',
            color: '#f9c300',
            cursor: 'pointer'
          }}
          onClick={() => setMostrarLogin(false)}
        >
          Volver
        </button>
      </form>
    ) : (
      <form onSubmit={handleRegistroSubmit}>
        <h3 style={{ marginBottom: 16 }}>Registro</h3>
        <input
          type="text"
          name="nombre"
          placeholder="Nombre"
          value={registroData.nombre}
          onChange={handleRegistroChange}
          required
          style={{
            width: '90%',
            padding: '8px',
            marginBottom: '10px',
            borderRadius: '6px',
            border: '1px solid #ccc'
          }}
        />
        <input
          type="text"
          name="apellido"
          placeholder="Apellido"
          value={registroData.apellido}
          onChange={handleRegistroChange}
          required
          style={{
            width: '90%',
            padding: '8px',
            marginBottom: '10px',
            borderRadius: '6px',
            border: '1px solid #ccc'
          }}
        />
        <input
          type="email"
          name="email"
          placeholder="E-mail"
          value={registroData.email}
          onChange={handleRegistroChange}
          required
          style={{
            width: '90%',
            padding: '8px',
            marginBottom: '10px',
            borderRadius: '6px',
            border: '1px solid #ccc'
          }}
        />
        <input
          type="tel"
          name="celular"
          placeholder="Celular"
          value={registroData.celular}
          onChange={handleRegistroChange}
          required
          style={{
            width: '90%',
            padding: '8px',
            marginBottom: '10px',
            borderRadius: '6px',
            border: '1px solid #ccc'
          }}
        />
        <input
          type="password"
          name="password"
          placeholder="Contraseña"
          value={registroData.password}
          onChange={handleRegistroChange}
          required
          style={{
            width: '90%',
            padding: '8px',
            marginBottom: '10px',
            borderRadius: '6px',
            border: '1px solid #ccc'
          }}
        />
        <input
          type="password"
          name="confirmPassword"
          placeholder="Confirmar contraseña"
          value={registroData.confirmPassword}
          onChange={handleRegistroChange}
          required
          style={{
            width: '90%',
            padding: '8px',
            marginBottom: '16px',
            borderRadius: '6px',
            border: '1px solid #ccc'
          }}
        />
        <button
          type="submit"
          style={{
            width: '100%',
            padding: '8px',
            borderRadius: '6px',
            border: '1px solid #f9c300',
            background: '#f9c300',
            color: '#fff',
            cursor: 'pointer'
          }}
        >
          Registrar
        </button>
        <button
          type="button"
          style={{
            width: '100%',
            marginTop: '8px',
            padding: '8px',
            borderRadius: '6px',
            border: '1px solid #ccc',
            background: '#fff',
            color: '#f9c300',
            cursor: 'pointer'
          }}
          onClick={() => setMostrarRegistro(false)}
        >
          Volver
        </button>
      </form>
    )}
  </div>
)}
      </header>

      <main style={{ paddingTop: "60px" }}>
        <div className="funciones" style={{ display: 'flex', gap: '20px', justifyContent: 'center', margin: '20px 0' }}>
          <div style={{ position: 'relative' }}>
            <button
              className="funcion-boton"
              onClick={() => setPestanaActiva(pestanaActiva === 'destino' ? null : 'destino')}
            >
              <img src="https://img.icons8.com/ios-filled/50/ffffff/airport.png" alt="avión" />
              ¿A dónde vas? {destino && `→ ${destino}`}
            </button>

            {pestanaActiva === 'destino' && (
              <div className="popup-destinos">
                <h3 className="popup-titulo">Destinos Populares</h3>
                <ul className="popup-lista">
                  {[
                    "Acapulco, Guerrero - México",
                    "Cancún, Quintana Roo - México",
                    "Cuernavaca, Morelos - México",
                    "Puerto Vallarta, Jalisco - México",
                    "Huatulco, Oaxaca - México"
                  ].map((dest, i) => (
                    <li
                      key={i}
                      className={`popup-item ${destino === dest ? 'seleccionado' : ''}`}
                      onClick={() => {
                        setDestino(dest);
                        setPestanaActiva(null);
                      }}
                    >
                      <img
                        src="https://img.icons8.com/ios-filled/24/f9c300/marker.png"
                        alt="ubicación"
                        className="icono-ubicacion"
                      />
                      <span>{dest}</span>
                    </li>
                  ))}
                </ul>
              </div>
            )}
          </div>

          <div style={{ position: 'relative' }}>
            <button
              className="funcion-boton"
              onClick={() => setPestanaActiva(pestanaActiva === 'fechas' ? null : 'fechas')}
            >
              <img src="https://img.icons8.com/ios-filled/50/ffffff/calendar.png" alt="calendario" />
              {llegada && salida
                ? `${new Date(llegada).toLocaleDateString()} - ${new Date(salida).toLocaleDateString()}`
                : 'Llegada - Salida'}
            </button>

            {pestanaActiva === 'fechas' && (
              <div className="pestana-emergente" style={{ position: 'absolute', top: '50px', left: 0 }}>
                <label>
                  <b>Llegada:</b>
                  <input
                    type="datetime-local"
                    className="campo-fecha"
                    value={llegada}
                    onChange={e => setLlegada(e.target.value)}
                  />
                </label>
                <br />
                <label>
                  <b>Salida:</b>
                  <input
                    type="datetime-local"
                    className="campo-fecha"
                    value={salida}
                    onChange={e => setSalida(e.target.value)}
                  />
                </label>
              </div>
            )}
          </div>

          <div style={{ position: 'relative' }}>
            <button
              className="funcion-boton"
              onClick={() => setPestanaActiva(pestanaActiva === 'personas' ? null : 'personas')}
            >
              <img src="https://img.icons8.com/ios-filled/50/ffffff/conference.png" alt="personas" />
              Niños - Adultos - Habitaciones
            </button>

            {pestanaActiva === 'personas' && (
              <div className="pestana-emergente-huespedes">
                <div className="fila-huespedes">
                  <span className="etiqueta">Adultos</span>
                  <div className="control">
                    <button onClick={() => setAdultos(Math.max(1, adultos - 1))}>-</button>
                    <span>{adultos}</span>
                    <button onClick={() => setAdultos(adultos + 1)}>+</button>
                  </div>
                </div>
                <hr />
                <div className="fila-huespedes">
                  <span className="etiqueta">Niños</span>
                  <div className="control">
                    <button onClick={() => setNinos(Math.max(0, ninos - 1))}>-</button>
                    <span>{ninos}</span>
                    <button onClick={() => setNinos(ninos + 1)}>+</button>
                  </div>
                </div>
                <hr />
                <div className="fila-huespedes">
                  <span className="etiqueta">Habitaciones</span>
                  <div className="control">
                    <button onClick={() => setHabitaciones(Math.max(1, habitaciones - 1))}>-</button>
                    <span>{habitaciones}</span>
                    <button onClick={() => setHabitaciones(habitaciones + 1)}>+</button>
                  </div>
                </div>
                <button className="boton-buscar-popup" onClick={handleBuscar}>Buscar</button>
              </div>
            )}
          </div>
        </div>

        <h2 style={{ textAlign: 'center' }}>Resultados para: {destino}</h2>

        <div style={{ textAlign: 'center', marginBottom: '20px' }}>
          <p>
            <b>Fechas:</b> {llegada && new Date(llegada).toLocaleDateString()} - {salida && new Date(salida).toLocaleDateString()}<br />
            <b>Adultos:</b> {adultos} &nbsp;
            <b>Niños:</b> {ninos} &nbsp;
            <b>Habitaciones:</b> {habitaciones}
          </p>
        </div>

        <div className="lista-hoteles" style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'center', gap: '20px' }}>
          {hoteles.length === 0 ? (
            <p style={{ textAlign: 'center' }}>No se encontraron hoteles para tu búsqueda.</p>
          ) : (
            hoteles.map(hotel => (
              <div
                key={hotel.id}
                className="hotel-card"
                onClick={() => irAHotel(hotel.id)}
                style={{
                  border: '2px solid #00163a',
                  borderRadius: '10px',
                  padding: '20px',
                  width: '250px',
                  cursor: 'pointer',
                  backgroundColor: '#fff',
                  color: '#00163a',
                  boxShadow: '0 4px 10px rgba(0, 0, 0, 0.1)',
                  transition: 'transform 0.2s'
                }}
                onMouseEnter={e => e.currentTarget.style.transform = 'scale(1.05)'}
                onMouseLeave={e => e.currentTarget.style.transform = 'scale(1)'}
              >
                <h3 style={{ marginBottom: '10px' }}>{hotel.nombre}</h3>
                <p style={{ marginBottom: '8px' }}>Calificación: {hotel.calificacion}</p>
                {hotel.descripcion && <p>{hotel.descripcion}</p>}
              </div>
            ))
          )}
        </div>
      </main>

      <footer style={{ background: '#00163a', color: '#fff', padding: '20px', textAlign: 'center', marginTop: '30px' }}>
        <p><b>©2025 ClickRoom. Todos los derechos reservados.</b></p>
        <p>Desarrollado por Siminbikini</p>
      </footer>
    </div>
  );
}

export default Resultados;
