import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import './App.css';
import logo from './img/ClickRoom.png';
import perfilImg from './img/perfil.jpg';

function Hotel() {
  const { id } = useParams();
  const navigate = useNavigate();

  const [hotel, setHotel] = useState(null);

  // Estados para filtros
  const [pestanaActiva, setPestanaActiva] = useState(null);
  const [destino, setDestino] = useState('');
  const [llegada, setLlegada] = useState('');
  const [salida, setSalida] = useState('');
  const [adultos, setAdultos] = useState(2);
  const [ninos, setNinos] = useState(0);
  const [habitaciones, setHabitaciones] = useState(1);
  // Estados para login y registro
  const [perfilAbierto, setPerfilAbierto] = useState(false);
  const [mostrarLogin, setMostrarLogin] = useState(false);
  const [mostrarRegistro, setMostrarRegistro] = useState(false);
  const [usuario, setUsuario] = useState(null);
  const [popupReservasAbierto, setPopupReservasAbierto] = useState(false);
  const [reservas, setReservas] = useState([]);

  const [loginData, setLoginData] = useState({ email: '', password: '' });
  const [registroData, setRegistroData] = useState({
    nombre: '',
    apellido: '',
    email: '',
    celular: '',
    password: '',
    confirmPassword: ''
  });

  useEffect(() => {
    fetch(`/hotel/search/${id}`)
      .then(res => res.json())
      .then(data => setHotel(data))
      .catch(err => console.error("Error al obtener detalles del hotel", err));
  }, [id]);

  const handleBuscar = () => {
    const params = new URLSearchParams({
      destino,
      llegada,
      salida,
      adultos,
      ninos,
      habitaciones
    });
    navigate(`/resultados?${params.toString()}`);
    setPestanaActiva(null);
  };
  const handleGlobalSearch = (e) => {
  if (e.key && e.key !== 'Enter') return;

  if (!destino || destino.trim() === '') {
    alert("Por favor ingresa un destino.");
    return;
  }

  const params = new URLSearchParams({
    destino
  });

  navigate(`/resultados?${params.toString()}`);

  if (setPestanaActiva) {
    setPestanaActiva(null);
  }
};
  const handleLoginChange = (e) => {
    setLoginData({ ...loginData, [e.target.name]: e.target.value });
  };
  const handleLoginSubmit = (e) => {
    e.preventDefault();
    alert(`Email: ${loginData.email}\nContraseña: ${loginData.password}`);
    setLoginData({ email: '', password: '' });
    setMostrarLogin(false);
    setPerfilAbierto(false);
  };
  const handleRegistroChange = (e) => {
    setRegistroData({ ...registroData, [e.target.name]: e.target.value });
  };
  const handleRegistroSubmit = (e) => {
    e.preventDefault();
    alert(
      `Nombre: ${registroData.nombre}\nApellido: ${registroData.apellido}\nEmail: ${registroData.email}\nCelular: ${registroData.celular}\nContraseña: ${registroData.password}\nConfirmación: ${registroData.confirmPassword}`
    );
    setRegistroData({
      nombre: '',
      apellido: '',
      email: '',
      celular: '',
      password: '',
      confirmPassword: ''
    });
    setMostrarRegistro(false);
    setPerfilAbierto(false);
  };

  if (!hotel) return <div className="App">Cargando...</div>;

  return (
    <div className="App">
      <header className="App-header">
        <div className="logo">
          <a href="/">
            <img src={logo} alt="logo" />
          </a>
        </div>

        {/* Barra de búsqueda */}
        <div className="search-bar">
          <input
            type="text"
            placeholder="¿A dónde vas?"
            value={destino}
            onChange={e => setDestino(e.target.value)}
            onKeyDown={handleGlobalSearch}
          />
          <button
            onClick={handleGlobalSearch}
            className="boton-buscar-popup"
            style={{ marginLeft: '10px' }}
          >
            Buscar
          </button>
        </div>

        <div className="header-icons">
          <button
            onClick={() => setPopupReservasAbierto(!popupReservasAbierto)}
          >
            <span role="img" aria-label="reservas" style={{ fontSize: 24 }}>
              🛏️
            </span>
          </button>
          <img
            src={perfilImg}
            alt="perfil"
            style={{ cursor: 'pointer', borderRadius: '50%' }}
            onClick={() => {
              setPerfilAbierto(!perfilAbierto);
              setMostrarLogin(false);
              setMostrarRegistro(false);
            }}
          />
        </div>
        {perfilAbierto && (
  <div
    className="pestana-emergente"
    style={{
      position: 'absolute',
      top: '50px',
      right: 0,
      background: '#fff',
      color: '#222',
      borderRadius: '10px',
      boxShadow: '0 2px 8px rgba(0,0,0,0.15)',
      padding: '20px',
      zIndex: 2000,
      minWidth: '240px',
      textAlign: 'center'
    }}
  >
    {!mostrarLogin && !mostrarRegistro ? (
      <>
        <img
          src={perfilImg}
          alt="Perfil"
          style={{ width: 60, height: 60, borderRadius: '50%', marginBottom: 16, objectFit: 'cover' }}
        />
        <div style={{ marginBottom: 12 }}>
          <button
            style={{
              width: '100%',
              marginBottom: 8,
              padding: '8px',
              borderRadius: '6px',
              border: '1px solid #f9c300',
              background: '#f9c300',
              color: '#fff',
              cursor: 'pointer'
            }}
            onClick={() => {
              setMostrarLogin(true);
              setMostrarRegistro(false);
            }}
          >
            Iniciar sesión
          </button>
          <button
            style={{
              width: '100%',
              padding: '8px',
              borderRadius: '6px',
              border: '1px solid #f9c300',
              background: '#fff',
              color: '#f9c300',
              cursor: 'pointer'
            }}
            onClick={() => {
              setMostrarRegistro(true);
              setMostrarLogin(false);
            }}
          >
            Registrarse
          </button>
        </div>
      </>
    ) : mostrarLogin ? (
      <form onSubmit={handleLoginSubmit}>
        <h3 style={{ marginBottom: 16 }}>Iniciar sesión</h3>
        <input
          type="email"
          name="email"
          placeholder="E-mail"
          value={loginData.email}
          onChange={handleLoginChange}
          required
          style={{
            width: '90%',
            padding: '8px',
            marginBottom: '10px',
            borderRadius: '6px',
            border: '1px solid #ccc'
          }}
        />
        <input
          type="password"
          name="password"
          placeholder="Contraseña"
          value={loginData.password}
          onChange={handleLoginChange}
          required
          style={{
            width: '90%',
            padding: '8px',
            marginBottom: '16px',
            borderRadius: '6px',
            border: '1px solid #ccc'
          }}
        />
        <button
          type="submit"
          style={{
            width: '100%',
            padding: '8px',
            borderRadius: '6px',
            border: '1px solid #f9c300',
            background: '#f9c300',
            color: '#fff',
            cursor: 'pointer'
          }}
        >
          Iniciar sesión
        </button>
        <button
          type="button"
          style={{
            width: '100%',
            marginTop: '8px',
            padding: '8px',
            borderRadius: '6px',
            border: '1px solid #ccc',
            background: '#fff',
            color: '#f9c300',
            cursor: 'pointer'
          }}
          onClick={() => setMostrarLogin(false)}
        >
          Volver
        </button>
      </form>
    ) : (
      <form onSubmit={handleRegistroSubmit}>
        <h3 style={{ marginBottom: 16 }}>Registro</h3>
        <input
          type="text"
          name="nombre"
          placeholder="Nombre"
          value={registroData.nombre}
          onChange={handleRegistroChange}
          required
          style={{
            width: '90%',
            padding: '8px',
            marginBottom: '10px',
            borderRadius: '6px',
            border: '1px solid #ccc'
          }}
        />
        <input
          type="text"
          name="apellido"
          placeholder="Apellido"
          value={registroData.apellido}
          onChange={handleRegistroChange}
          required
          style={{
            width: '90%',
            padding: '8px',
            marginBottom: '10px',
            borderRadius: '6px',
            border: '1px solid #ccc'
          }}
        />
        <input
          type="email"
          name="email"
          placeholder="E-mail"
          value={registroData.email}
          onChange={handleRegistroChange}
          required
          style={{
            width: '90%',
            padding: '8px',
            marginBottom: '10px',
            borderRadius: '6px',
            border: '1px solid #ccc'
          }}
        />
        <input
          type="tel"
          name="celular"
          placeholder="Celular"
          value={registroData.celular}
          onChange={handleRegistroChange}
          required
          style={{
            width: '90%',
            padding: '8px',
            marginBottom: '10px',
            borderRadius: '6px',
            border: '1px solid #ccc'
          }}
        />
        <input
          type="password"
          name="password"
          placeholder="Contraseña"
          value={registroData.password}
          onChange={handleRegistroChange}
          required
          style={{
            width: '90%',
            padding: '8px',
            marginBottom: '10px',
            borderRadius: '6px',
            border: '1px solid #ccc'
          }}
        />
        <input
          type="password"
          name="confirmPassword"
          placeholder="Confirmar contraseña"
          value={registroData.confirmPassword}
          onChange={handleRegistroChange}
          required
          style={{
            width: '90%',
            padding: '8px',
            marginBottom: '16px',
            borderRadius: '6px',
            border: '1px solid #ccc'
          }}
        />
        <button
          type="submit"
          style={{
            width: '100%',
            padding: '8px',
            borderRadius: '6px',
            border: '1px solid #f9c300',
            background: '#f9c300',
            color: '#fff',
            cursor: 'pointer'
          }}
        >
          Registrar
        </button>
        <button
          type="button"
          style={{
            width: '100%',
            marginTop: '8px',
            padding: '8px',
            borderRadius: '6px',
            border: '1px solid #ccc',
            background: '#fff',
            color: '#f9c300',
            cursor: 'pointer'
          }}
          onClick={() => setMostrarRegistro(false)}
        >
          Volver
        </button>
      </form>
    )}
  </div>
)}
      {popupReservasAbierto && (
  <div
    className="pestana-emergente"
    style={{
      position: 'absolute',
      top: '50px',
      right: '50px',
      background: '#fff',
      color: '#222',
      borderRadius: '10px',
      boxShadow: '0 2px 8px rgba(0,0,0,0.15)',
      padding: '20px',
      zIndex: 2000,
      minWidth: '260px',
      textAlign: 'center'
    }}
  >
    {!usuario ? (
      <>
        <p>No has iniciado sesión. Por favor, inicia sesión para ver tus reservas.</p>
        <button
          style={{
            width: '100%',
            marginBottom: '10px',
            padding: '8px',
            borderRadius: '6px',
            border: '1px solid #f9c300',
            background: '#f9c300',
            color: '#fff',
            cursor: 'pointer'
          }}
          onClick={() => {
            setPopupReservasAbierto(false);
            setPerfilAbierto(true);
            setMostrarLogin(true);
          }}
        >
          Iniciar sesión
        </button>
        <p style={{ fontSize: '0.9em', margin: '10px 0' }}>
          o, si no te has registrado, ¡regístrate!
        </p>
        <button
          style={{
            width: '100%',
            padding: '8px',
            borderRadius: '6px',
            border: '1px solid #f9c300',
            background: '#fff',
            color: '#f9c300',
            cursor: 'pointer'
          }}
          onClick={() => {
            setPopupReservasAbierto(false);
            setPerfilAbierto(true);
            setMostrarRegistro(true);
          }}
        >
          Registrarse
        </button>
      </>
    ) : (
      <>
        <h3>Mis Reservas</h3>
        {reservas.length === 0 ? (
          <p>No tienes reservas registradas.</p>
        ) : (
          <ul style={{ listStyle: 'none', padding: 0, maxHeight: '200px', overflowY: 'auto' }}>
            {reservas.map((r, i) => (
              <li
                key={i}
                style={{
                  marginBottom: '10px',
                  padding: '10px',
                  border: '1px solid #ccc',
                  borderRadius: '6px'
                }}
              >
                <strong>{r.hotel}</strong><br />
                {new Date(r.llegada).toLocaleDateString()} - {new Date(r.salida).toLocaleDateString()}<br />
                Estado: {r.estado}
              </li>
            ))}
          </ul>
        )}
      </>
    )}
  </div>
