import React, { useState } from 'react';
import { Routes, Route } from 'react-router-dom';
import './App.css';
import logo from './img/ClickRoom.png';
import perfilImg from './img/perfil.jpg';
import Resultados from './Resultados';
import Carousel from './Carousel';
import Hotel from './Hotel';
import Login from './components/Login';
import Register from './components/Register';

function App() {
  const [destino, setDestino] = useState('');
  const [showLogin, setShowLogin] = useState(false);
  const [showRegister, setShowRegister] = useState(false);

  const handleSearch = () => {
    window.location.href = `/resultados?destino=${encodeURIComponent(destino)}`;
  };

  return (
    <div className="App">
      <header className="App-header">
        {/* IZQUIERDA: Logo */}
        <div className="logo">
          <img src={logo} alt="logo" />
        </div>

        {/* CENTRO: Buscador */}
        <div className="search-bar">
          <input
            className="search-bar-input"
            placeholder="¿A dónde vas?"
            value={destino}
            onChange={(e) => setDestino(e.target.value)}
          />
          <button className="funcion-boton" onClick={handleSearch}>
            Buscar
          </button>
        </div>

        {/* DERECHA: Icono de perfil */}
        <div className="header-icons">
          <img
            src={perfilImg}
            alt="perfil"
            onClick={() => setShowLogin(true)}
            style={{ cursor: "pointer", borderRadius: "50%" }}
          />
        </div>
      </header>

      {/* Modales */}
      {showLogin && <Login onClose={() => setShowLogin(false)} />}
      {showRegister && <Register onClose={() => setShowRegister(false)} />}

      {/* Contenido principal */}
      <div style={{ paddingTop: "60px" }}>
        <Routes>
          <Route path="/" element={<Carousel />} />
          <Route path="/resultados" element={<Resultados />} />
          <Route path="/hotel/:id" element={<Hotel />} />
        </Routes>
      </div>
    </div>
  );
}

export default App;
